mapper.select:
SELECT
  PT.product_type_id, PT.name, PT.uid, PT.create_time, PT.update_time, count(P.product_type_id) count
FROM product_type PT
LEFT JOIN product P ON P.product_type_id=PT.product_type_id

    PT.uid=#{uid}

      AND PT.create_time BETWEEN #{startDate} AND #{endDate}




        GROUP BY PT.name



    GROUP BY PT.create_time  ${sortOrder}
    GROUP BY PT.update_time  ${sortOrder}, PT.name
    GROUP BY PT.name  ${sortOrder}

limit #{start}, #{size}

SELECT count(0) FROM product_type

      uid=#{uid}

          AND create_time BETWEEN #{startDate} AND #{endDate}

mapper.delete:
DELETE FROM product_type

    product_type_id in

        #{id}

    AND uid=#{uid}
